<?php
 class Auth{public static function login(){$result=false;if($_SERVER['HTTP_AUTHORIZATION']){list($scheme,$text)=explode(' ',$_SERVER['HTTP_AUTHORIZATION']);$text=base64_decode($text);list($username,$password)=explode(':',$text);require_once('../inc/api/account.php');$account=new MerakAccount();$result=$account->AuthenticateUser($username,$password,$SERVER_NAME);if($result)$result=$account->GetProperty('u_admin');}if(!$result){header('Status: 401 Access Denied');header('Authorization: Basic');header('WWW-Authenticate: Basic realm="Tools"');die();}}}?>
