<?php  require_once"inc/syntax/clswebservicegrid.php";class cFTPSyncGrid extends cWebserviceGrid{function cFTPSyncGrid($option){$this->Name="ftpsync";$this->Filename=$_SESSION["INSTALLPATH"].$option["SOURCE"][0]["FILE"][0]["ATTRIBUTES"]["NAME"];}function LoadFromXML(){if(file_exists($this->Filename)){$parser=new ParseXML();$this->xml=$parser->GetXMLTree($this->Filename);$items=$this->xml["FTP"][0]["SYNC"][0]["ITEM"];unset($parser);}if($items)foreach($items as$index=>$host)if($host)foreach($host as$var=>$value)if($var!="OPTIONS"&&$var!="SCHEDULE"&&$var!="DESTINATION"&&$var!="SOURCE"&&$var!="VALUE")$return[strtolower($index)][strtolower($var)]=$this->xml2arr($value,$var);else if($var!="VALUE"){$pom=$this->xml2arr($value,$var);if($pom)foreach($pom as$pomkey=>$pomval)$return[strtolower($index)][$pomkey]=$pomval;}$this->Data=$return;}function saveToXML(){if($this->Data[0])foreach($this->Data as$index=>$host)foreach($host as$var=>$value){if(strpos($var,'schedule_')!==false)$return[$index]['SCHEDULE'][0]['ATTRIBUTES'][strtoupper(str_replace('schedule_','',$var))]=strtoupper(str_replace('schedule_','',$var))!='AT'?intval($value):$value;else if(strpos($var,'source_')!==false){$return[$index]['SOURCE'][0]['ATTRIBUTES'][strtoupper(str_replace('source_','',$var))]=(strtoupper(str_replace('source_','',$var))=='RECURSIVE'||strtoupper(str_replace('source_','',$var))=='CLONE')?intval($value):$value;}else if(strpos($var,'destination_')!==false){$return[$index]['DESTINATION'][0]['ATTRIBUTES'][strtoupper(str_replace('destination_','',$var))]=$value;}else if(!($var=='value'&&!isset($value[0]["VALUE"])))$return[$index][strtoupper($var)]=$this->arr2xml($value,strtolower($var));}return$return;}function Save(){$arr=$this->SaveToXML();$parser=new ParseXML();if(file_exists($this->Filename))$this->xml=$parser->GetXMLTree($this->Filename);$this->xml["FTP"]=self::htmlspecialchars_array($this->xml["FTP"]);$this->xml["FTP"][0]["SYNC"][0]["ITEM"]=$arr;$parser=new ParseXML();$xmlstr=$parser->Array2