<?php  require_once"inc/syntax/clsstdgrid.php";require_once"inc/api/gw.php";class cGreyListGrid extends cStdGrid{function cGreyListGrid($option,$name,$count=0,$type=false,$owner=false,$value=false){$this->Name=$name;$this->Item=new cStdItem(8,";SEP;SEP;SEP;SEP;SEP;SEP;");$this->type=intval($type);$this->owner=$owner;$this->value=$value;$this->counter=intval($count);if(!$this->counter){$this->counter=100;}$this->noLink=true;}function getBuffer(){global$api;$query=' ';$spam_settings=parsedatfile('spam.dat','spam2');$pending_hours=$spam_settings['SpamGLPending'];$t=time();$this->d_day=MerakGWAPI::unix2calendarDate($t);$this->d_time=MerakGWAPI::unix2calendarTime($t);$this->d_time=$this->d_time*60;$this->d_daytosecond=86400;$this->d_pending=$pending_hours*3600;$this->expired='AND ((ipCreationDate-%d)*%d+ (ipCreationTime - %d)+%d <=0) ';$this->pending='AND ((ipCreationDate-%d)*%d + (ipCreationTime - %d)+%d >0) ';$expired=sprintf($this->expired,$this->d_day,$this->d_daytosecond,$this->d_time,$this->d_pending);$pending=sprintf($this->pending,$this->d_day,$this->d_daytosecond,$this->d_time,$this->d_pending);switch($this->type){case 0:$query.="WHERE (ipAuthorized = '0') ".$pending;break;case 1:$query.="WHERE (ipAuthorized = '1') ";break;case 2:$query="WHERE (ipAuthorized = '0') ".$expired;break;case 3:$query="WHERE (ipAuthorized = '1' OR ipAuthorized = '0') ";break;}if($_SESSION['ACCOUNT']=='DOMAINADMIN'){if(strpos($this->owner,"@")!==false){$owner=substr($this->owner,0,strpos($this->owner,"@"));}$list=getdomainlist();$ownerquery='(';foreach($list as$dom){$ownerquery.=" ipEmail LIKE '".$owner."%".$dom['DOMAIN']."'";if($count++<count($list)-1)$ownerquery.=" OR ";}$ownerquery.=')';$query.="AND ".$ownerquery;}if($this->value){$query.=($query==' '?'WHERE':'AND')." ipAddress LIKE '".$this->value."%'";}$this->Buffer=$api->GLList('',$query,$this->counter);}function loadFromBuffer(){if($this->Buffer){$ic=0;$lines=explode(CRLF,$this->Buffer);for($i=0;$i<count($lines);$i++){$item=array